func TestIVSID3(t *testing.T) {
	vals, err := ReadID3(bytes.NewReader(ivsID3))
	if err != nil {
		t.Fatalf("Failed read: %v", err)
	}
	if v, ok := vals["TRCK"]; ok {
		if v != "581" {
			t.Fatalf("Wrong value expected \"581\" got %v", v)
		}
	} else {
		t.Fatal("missing key TRCK")
	}
	if _, ok := vals["TXXX"]; !ok {
		t.Fatal("missing key TXXX")
	}
}
